#merge
a=list.files("./15_list")
dir=paste("15_list/",a,sep="")
n=length(dir)
merge1<-read.table("15_list/1210",header=T,stringsAsFactors = F)
for (i in 2:n){
merge2 <- read.table(file=dir[i],stringsAsFactors = F,header=T)
merge1 <- merge(merge1,merge2,all=TRUE,by="f.eid")
}
bb<-merge1[,c(1,1)]
names(bb)<-c("FID","IID")
write.table(bb,"15_list_indiv",quote=F,row.names=F)
